const items = $input.all();
if (items.length === 0) {
  return [{json: {error: 'No grouped data found'}}];
}


const headers = Object.keys(items[0].json);


const categoryPatterns = [
  { pattern: /^category$/i, type: 'Category' },
  { pattern: /^ifc[\s_-]?type$/i, type: 'IFC' },
  { pattern: /^host[\s_-]?category$/i, type: 'Host' },
  { pattern: /^ifc[\s_-]?export[\s_-]?as$/i, type: 'Export' },
  { pattern: /^layer$/i, type: 'Layer' }
];

let categoryField = null;
let categoryFieldType = 'None';


for (const header of headers) {
  for (const {pattern, type} of categoryPatterns) {
    if (pattern.test(header)) {
      categoryField = header;
      categoryFieldType = type;
      break;
    }
  }
  if (categoryField) break;
}


const volumetricPatterns = /volume|area|length|count|quantity|thickness|perimeter|depth|size|dimension|weight|mass/i;
const volumetricFields = headers.filter(header => volumetricPatterns.test(header));


const categoryValues = new Set();
if (categoryField) {
  items.forEach(item => {
    const value = item.json[categoryField];
    if (value && value !== '' && value !== null) {
      categoryValues.add(value);
    }
  });
}

console.log('Category field analysis:');
console.log('- Field found:', categoryField || 'None');
console.log('- Field type:', categoryFieldType);
console.log('- Unique values:', categoryValues.size);
console.log('- Volumetric fields:', volumetricFields.length);


return [{
  json: {
    categoryField: categoryField,
    categoryFieldType: categoryFieldType,
    categoryValues: Array.from(categoryValues),
    volumetricFields: volumetricFields,
    groupedData: items.map(item => item.json),
    totalGroups: items.length
  }
}];

const categoryInfo = $node['Find Category Fields'].json;
const groupedData = categoryInfo.groupedData;
const categoryField = categoryInfo.categoryField;
const volumetricFields = categoryInfo.volumetricFields || [];


let classifications = {};
let buildingCategories = [];
let drawingCategories = [];

try {
  const aiResponse = $input.first().json;
  const content = aiResponse.content || aiResponse.message || aiResponse.response || '';

  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    const parsed = JSON.parse(jsonMatch[0]);
    classifications = parsed.classifications || {};
    buildingCategories = parsed.building_categories || [];
    drawingCategories = parsed.drawing_categories || [];
    console.log(`AI classified ${Object.keys(classifications).length} categories`);
    console.log(`Building categories: ${buildingCategories.length}`);
    console.log(`Drawing categories: ${drawingCategories.length}`);
  }
} catch (error) {
  console.error('Error parsing AI classification:', error.message);
}


return groupedData.map(group => {
  let isBuildingElement = false;
  let reason = '';
  let confidence = 0;

  if (categoryField && group[categoryField]) {

    const categoryValue = group[categoryField];

    if (classifications[categoryValue] !== undefined) {
      isBuildingElement = classifications[categoryValue];
      confidence = 95;
      reason = `Category '${categoryValue}' classified by AI as ${isBuildingElement ? 'building element' : 'drawing/annotation'}`;
    } else {

      const lowerCategory = categoryValue.toLowerCase();
      const drawingKeywords = /annotation|drawing|text|dimension|tag|arial|arial|sketchlines|divisionrules|labels|lines|sketch|view|sheet|grid|section|elevation|callout|revision|legend|symbol|mark|note|detail items|filled region|detail line/i;
      const buildingKeywords = /wall|floor|roof|column|beam|door|window|stair|pipe|duct|equipment|fixture|furniture/i;

      if (drawingKeywords.test(lowerCategory)) {
        isBuildingElement = false;
        confidence = 85;
        reason = `Category '${categoryValue}' matched drawing keywords`;
      } else if (buildingKeywords.test(lowerCategory)) {
        isBuildingElement = true;
        confidence = 85;
        reason = `Category '${categoryValue}' matched building keywords`;
      } else {

        isBuildingElement = true;
        confidence = 70;
        reason = `Category '${categoryValue}' assumed as building element (no clear match)`;
      }
    }
  } else {

    let hasSignificantVolumetricData = false;
    let volumetricCount = 0;

    for (const field of volumetricFields) {
      const value = parseFloat(group[field]);
      if (!isNaN(value) && value > 0) {
        hasSignificantVolumetricData = true;
        volumetricCount++;
      }
    }

    isBuildingElement = hasSignificantVolumetricData;
    confidence = hasSignificantVolumetricData ? 80 : 60;
    reason = hasSignificantVolumetricData ? 
      `Has ${volumetricCount} volumetric parameters with values` : 
      'No category field and no significant volumetric data';
  }

  return {
    json: {
      ...group,
      is_building_element: isBuildingElement,
      element_confidence: confidence,
      element_reason: reason
    }
  };
});

// Modified Group Data with AI Rules node with feet to meters conversion
const input = $input.first().json;
const aggregationRules = input.aggregationRules;
const headerMapping = input.headerMapping;
const rawData = input.rawData;
const groupByParamOriginal = input.groupByParam;

// Conversion factor from cubic feet to cubic meters
const CUBIC_FEET_TO_CUBIC_METERS = 0.0283168;

// Map the groupBy parameter
const groupByParam = headerMapping[groupByParamOriginal] || groupByParamOriginal;

console.log(`Grouping ${rawData.length} items by: ${groupByParam}`);

// Check if we have BoundingBox columns
const hasBoundingBox = Object.keys(headerMapping).some(key => 
  key.includes('BoundingBoxMin_X') || key.includes('BoundingBoxMax_X')
);

// Clean and prepare data
const cleanedData = rawData.map(item => {
  const cleaned = {};
  Object.entries(item).forEach(([key, value]) => {
    const newKey = headerMapping[key] || key;
    cleaned[newKey] = value;
  });

  // Calculate BoundingBox volume if we have the necessary columns
  if (hasBoundingBox) {
    // Extract coordinates (in feet)
    const minX = parseFloat(cleaned['BoundingBoxMin_X'] || 0);
    const minY = parseFloat(cleaned['BoundingBoxMin_Y'] || 0);
    const minZ = parseFloat(cleaned['BoundingBoxMin_Z'] || 0);
    const maxX = parseFloat(cleaned['BoundingBoxMax_X'] || 0);
    const maxY = parseFloat(cleaned['BoundingBoxMax_Y'] || 0);
    const maxZ = parseFloat(cleaned['BoundingBoxMax_Z'] || 0);

    // Calculate volume in cubic feet: (maxX - minX) * (maxY - minY) * (maxZ - minZ)
    const volumeCubicFeet = Math.abs((maxX - minX) * (maxY - minY) * (maxZ - minZ));

    // Convert to cubic meters
    const volumeCubicMeters = volumeCubicFeet * CUBIC_FEET_TO_CUBIC_METERS;

    cleaned['BoundingBox_Volume'] = volumeCubicMeters;

    console.log(`Item BoundingBox: ${volumeCubicFeet.toFixed(2)} ft¬≥ = ${volumeCubicMeters.toFixed(4)} m¬≥`);
  }

  return cleaned;
});

// Group the data
const grouped = {};

cleanedData.forEach(item => {
  const groupKey = item[groupByParam];

  if (!groupKey || groupKey === '' || groupKey === null) return;

  if (!grouped[groupKey]) {
    grouped[groupKey] = {
      _count: 0,
      _values: {},
      _boundingBoxVolumes: [] // Store individual volumes for summing (already in m¬≥)
    };

    // Initialize value arrays for all parameters
    Object.keys(aggregationRules).forEach(param => {
      if (param !== groupByParam) {
        grouped[groupKey]._values[param] = [];
      }
    });

    // Add BoundingBox_Volume to values if it exists
    if (hasBoundingBox) {
      grouped[groupKey]._values['BoundingBox_Volume'] = [];
    }
  }

  grouped[groupKey]._count++;

  // Store BoundingBox volume if calculated (already in m¬≥)
  if (item['BoundingBox_Volume'] !== undefined) {
    grouped[groupKey]._boundingBoxVolumes.push(item['BoundingBox_Volume']);
  }

  // Collect values for aggregation
  Object.entries(item).forEach(([key, value]) => {
    if (key === groupByParam) return;

    if (value !== null && value !== undefined && value !== '' && grouped[groupKey]._values[key]) {
      grouped[groupKey]._values[key].push(value);
    }
  });
});

// Process aggregation
const result = [];

Object.entries(grouped).forEach(([groupKey, groupData]) => {
  const aggregated = {
    [groupByParam]: groupKey,
    'Element Count': groupData._count
  };

  // Add summed BoundingBox_Volume if we have it (in m¬≥)
  if (groupData._boundingBoxVolumes.length > 0) {
    const totalBoundingBoxVolume = groupData._boundingBoxVolumes.reduce((sum, vol) => sum + vol, 0);
    aggregated['BoundingBox_Volume_Total'] = Math.round(totalBoundingBoxVolume * 10000) / 10000; // Round to 4 decimal places
    console.log(`Group ${groupKey} - Total BoundingBox Volume: ${aggregated['BoundingBox_Volume_Total'].toFixed(4)} m¬≥`);
  }

  // Process other aggregations
  Object.entries(groupData._values).forEach(([param, values]) => {
    // Skip BoundingBox_Volume as we've already handled it
    if (param === 'BoundingBox_Volume') return;

    // Special handling for BoundingBox min/max coordinates - use 'first' rule
    if (param.includes('BoundingBoxMin') || param.includes('BoundingBoxMax')) {
      aggregated[param] = values[0];
      return;
    }

    const rule = aggregationRules[param] || 'first';

    if (values.length === 0) {
      aggregated[param] = null;
      return;
    }

    switch(rule) {
      case 'sum':
        const numericValues = values.map(v => {
          const num = parseFloat(String(v).replace(',', '.'));
          return isNaN(num) ? 0 : num;
        });
        aggregated[param] = numericValues.reduce((a, b) => a + b, 0);

        if (aggregated[param] % 1 !== 0) {
          aggregated[param] = Math.round(aggregated[param] * 100) / 100;
        }
        break;

      case 'mean':
      case 'average':
        const avgValues = values.map(v => {
          const num = parseFloat(String(v).replace(',', '.'));
          return isNaN(num) ? null : num;
        }).filter(v => v !== null);

        if (avgValues.length > 0) {
          const avg = avgValues.reduce((a, b) => a + b, 0) / avgValues.length;
          aggregated[param] = Math.round(avg * 100) / 100;
        } else {
          aggregated[param] = values[0];
        }
        break;

      case 'first':
      default:
        aggregated[param] = values[0];
        break;
    }
  });

  result.push({ json: aggregated });
});

// Sort results
result.sort((a, b) => {
  const aVal = a.json[groupByParam];
  const bVal = b.json[groupByParam];
  if (aVal < bVal) return -1;
  if (aVal > bVal) return 1;
  return 0;
});

console.log(`\nGrouping complete:`);
console.log(`- Input items: ${cleanedData.length}`);
console.log(`- Output groups: ${result.length}`);
console.log(`- Parameters processed: ${Object.keys(aggregationRules).length}`);
if (hasBoundingBox) {
  console.log(`- BoundingBox volumes calculated (converted from ft¬≥ to m¬≥)`);
  console.log(`- Conversion factor used: 1 ft¬≥ = ${CUBIC_FEET_TO_CUBIC_METERS} m¬≥`);
}

// Summary of aggregation rules
const rulesSummary = { sum: [], mean: [], first: [] };
Object.entries(aggregationRules).forEach(([param, rule]) => {
  if (rulesSummary[rule]) rulesSummary[rule].push(param);
});

console.log('\nAggregation summary:');
if (rulesSummary.sum.length > 0) {
  console.log(`- SUM (${rulesSummary.sum.length}): ${rulesSummary.sum.slice(0, 5).join(', ')}${rulesSummary.sum.length > 5 ? '...' : ''}`);
}
if (rulesSummary.mean.length > 0) {
  console.log(`- MEAN (${rulesSummary.mean.length}): ${rulesSummary.mean.slice(0, 5).join(', ')}${rulesSummary.mean.length > 5 ? '...' : ''}`);
}
if (rulesSummary.first.length > 0) {
  console.log(`- FIRST (${rulesSummary.first.length}): ${rulesSummary.first.slice(0, 5).join(', ')}${rulesSummary.first.length > 5 ? '...' : ''}`);
}

return result;

const items = $input.all();
if (items.length === 0) {
 throw new Error('No data found in Excel file');
}


const allHeaders = new Set();
items.forEach(item => {
 Object.keys(item.json).forEach(key => allHeaders.add(key));
});


const headers = Array.from(allHeaders);
const cleanedHeaders = headers.map(header => {
 return header.replace(/:\s*(string|double|int|float|boolean|number)\s*$/i, '').trim();
});


const headerMapping = {};
headers.forEach((oldHeader, index) => {
 headerMapping[oldHeader] = cleanedHeaders[index];
});


const sampleValues = {};
cleanedHeaders.forEach((header, index) => {
 const originalHeader = headers[index];
 for (const item of items) {
   const value = item.json[originalHeader];
   if (value !== null && value !== undefined && value !== '') {
     sampleValues[header] = value;
     break;
   }
 }
 if (!sampleValues[header]) {
   sampleValues[header] = null;
 }
});

console.log(`Found ${headers.length} unique headers across ${items.length} items`);


return [{
 json: {
   headers: cleanedHeaders,
   originalHeaders: headers,
   headerMapping: headerMapping,
   sampleValues: sampleValues,
   totalRows: items.length,
   totalHeaders: headers.length,

   rawData: items.map(item => item.json)
 }
}];

const aiResponse = $input.first().json;
const headerData = $node['Extract Headers and Data1'].json;


let aiRules = {};
try {
 const content = aiResponse.content || aiResponse.message || aiResponse.response || '';
 console.log('AI Response received, length:', content.length);


 const jsonMatch = content.match(/\{[\s\S]*\}/);
 if (jsonMatch) {
   const parsed = JSON.parse(jsonMatch[0]);
   aiRules = parsed.aggregation_rules || parsed.parameter_aggregation || {};
   console.log(`AI provided ${Object.keys(aiRules).length} rules`);
 } else {
   console.warn('No JSON found in AI response');
 }
} catch (error) {
 console.error('Error parsing AI response:', error.message);
}


const finalRules = {};
headerData.headers.forEach(header => {
 if (aiRules[header]) {
   finalRules[header] = aiRules[header];
 } else {

   const lowerHeader = header.toLowerCase();

   if (lowerHeader.match(/volume|area|length|width|height|count|quantity|thickness|perimeter|depth|size|dimension|weight|mass|total|amount|number/)) {
     finalRules[header] = 'sum';
   } else if (lowerHeader.match(/price|rate|cost|coefficient|factor|percent|ratio|efficiency|avg|average|mean/)) {
     finalRules[header] = 'mean';
   } else {
     finalRules[header] = 'first';
   }
 }
});

const groupByParam = $node['Setup - Define file paths1'].json.group_by;

console.log(`\nAggregation rules summary:`);
console.log(`- Total headers: ${headerData.headers.length}`);
console.log(`- AI rules: ${Object.keys(aiRules).length}`);
console.log(`- Default rules: ${headerData.headers.length - Object.keys(aiRules).length}`);
console.log(`- Group by: ${groupByParam}`);


return [{
 json: {
   aggregationRules: finalRules,
   headerMapping: headerData.headerMapping,
   headers: headerData.headers,
   originalHeaders: headerData.originalHeaders,
   rawData: headerData.rawData,
   groupByParam: groupByParam,
   totalRows: headerData.totalRows
 }
}];

// Enhanced Excel styling configuration
const excelBuffer = $input.first().binary.data;
const fileName = `Price_Estimation_Report_${new Date().toISOString().slice(0,10)}.xlsx`;

// Add metadata to the file
const metadata = {
  title: 'Price Estimation Analysis Report',
  author: 'DataDrivenConstruction.io',
  company: 'Automated Price Estimation System',
  created: new Date().toISOString(),
  description: 'Comprehensive project cost assessment with multi-standard material classification',
  keywords: 'Price, Cost Estimation, Building Materials'
};

// Return the enhanced Excel file
return [{
  json: {
    fileName: fileName,
    fileSize: excelBuffer.data.length,
    sheets: 8,
    metadata: metadata,
    timestamp: new Date().toISOString()
  },
  binary: {
    data: {
      ...excelBuffer,
      fileName: fileName,
      fileExtension: 'xlsx',
      mimeType: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
    }
  }
}];

// Prepare comprehensive data for multi-sheet Excel export
const items = $input.all();
const context = $getWorkflowStaticData('global').classificationSummary || {};
const projectFile = $node['Setup - Define file paths1'].json.project_file || '';
const classificationName = $node['Setup - Define file paths1'].json.classification_name || 'Unknown';

// Helper function to clean values
const cleanValue = (value) => {
  if (value === null || value === undefined) return '';
  return value;
};

// Calculate statistics
const totalGroups = items.length;
const totalElements = items.reduce((sum, item) => sum + (parseInt(item.json['Element Count']) || 0), 0);
const classifiedGroups = items.filter(item => 
  item.json['Classification_Code'] !== 'ERROR' && 
  item.json['Classification_Code'] !== 'Unclassified'
).length;

// Group by classification for summary
const byClassification = {};
items.forEach(item => {
  const code = item.json['Classification_Code'] || 'Unclassified';
  if (!byClassification[code]) {
    byClassification[code] = {
      name: item.json['Classification_Name'],
      count: 0,
      elements: 0,
      confidence: []
    };
  }
  byClassification[code].count++;
  byClassification[code].elements += parseInt(item.json['Element Count']) || 0;
  byClassification[code].confidence.push(parseFloat(item.json['Classification_Confidence']) || 0);
});

// Calculate average confidence per class
Object.keys(byClassification).forEach(code => {
  const confidences = byClassification[code].confidence;
  byClassification[code].avgConfidence = confidences.reduce((a, b) => a + b, 0) / confidences.length;
});

// Sheet 1: Executive Summary
const executiveSummary = [
  {
    'Metric': 'Project File',
    'Value': projectFile.split('\\').pop(),
    'Category': 'Project Information'
  },
  {
    'Metric': 'Classification System',
    'Value': classificationName,
    'Category': 'Project Information'
  },
  {
    'Metric': 'Project Type',
    'Value': context.projectType || 'Unknown',
    'Category': 'Project Information'
  },
  {
    'Metric': 'Total Element Groups',
    'Value': totalGroups,
    'Category': 'Statistics'
  },
  {
    'Metric': 'Total Individual Elements',
    'Value': totalElements,
    'Category': 'Statistics'
  },
  {
    'Metric': 'Successfully Classified',
    'Value': classifiedGroups,
    'Category': 'Statistics'
  },
  {
    'Metric': 'Classification Rate (%)',
    'Value': ((classifiedGroups/totalGroups)*100).toFixed(2),
    'Category': 'Statistics'
  },
  {
    'Metric': 'Unique Classification Codes',
    'Value': Object.keys(byClassification).length,
    'Category': 'Statistics'
  },
  {
    'Metric': 'Mapping Rules Available',
    'Value': context.totalMappingRules || 0,
    'Category': 'Mapping'
  },
  {
    'Metric': 'Items Using Mapping',
    'Value': items.filter(item => item.json['Mapping_Used'] === 'Yes').length,
    'Category': 'Mapping'
  },
  {
    'Metric': 'Average Confidence (%)',
    'Value': (items.reduce((sum, item) => sum + (parseFloat(item.json['Classification_Confidence']) || 0), 0) / items.length).toFixed(2),
    'Category': 'Quality'
  },
  {
    'Metric': 'Report Generated',
    'Value': new Date().toLocaleString(),
    'Category': 'Metadata'
  }
];

// Sheet 2: Classification Summary
const classificationSummary = Object.entries(byClassification)
  .sort((a, b) => b[1].count - a[1].count)
  .map(([code, data], index) => ({
    'Rank': index + 1,
    'Classification_Code': code,
    'Classification_Name': data.name || 'Unknown',
    'Group_Count': data.count,
    'Element_Count': data.elements,
    'Percentage_of_Total': ((data.count / totalGroups) * 100).toFixed(2),
    'Average_Confidence': data.avgConfidence.toFixed(2),
    'Confidence_Level': data.avgConfidence >= 80 ? 'High' : data.avgConfidence >= 60 ? 'Medium' : 'Low'
  }));

// Sheet 3: Detailed Classification Results
const detailedResults = items.map((item, index) => {
  const data = item.json;
  return {
    'Index': index + 1,
    'Element_Group': cleanValue(data['Type Name'] || data['Family'] || data['Category']),
    'Category': cleanValue(data['Category']),
    'Family': cleanValue(data['Family']),
    'Type_Name': cleanValue(data['Type Name']),
    'Element_Count': parseInt(data['Element Count']) || 0,
    'Classification_Code': cleanValue(data['Classification_Code']),
    'Classification_Name': cleanValue(data['Classification_Name']),
    'Classification_Level': cleanValue(data['Classification_Level']),
    'Parent_Class': cleanValue(data['Parent_Class']),
    'Confidence_%': parseFloat(data['Classification_Confidence']) || 0,
    'Mapping_Used': cleanValue(data['Mapping_Used']),
    'Alternative_Classes': cleanValue(data['Alternative_Classes']),
    'Classification_Reasoning': cleanValue(data['Classification_Reasoning']),
    'Status': cleanValue(data['Classification_Status']),
    'Timestamp': cleanValue(data['Classification_Timestamp'])
  };
});

// Sheet 4: Original Element Parameters (first 50 items with all original parameters)
const elementParameters = items.slice(0, Math.min(50, items.length)).map((item, index) => {
  const data = { ...item.json };
  // Remove internal fields
  delete data._systemPrompt;
  delete data._userPrompt;
  delete data._directMapping;
  delete data._originalData;

  return {
    'Index': index + 1,
    ...data
  };
});

// Sheet 5: Failed Classifications (if any)
const failedClassifications = items
  .filter(item => item.json['Classification_Status'] === 'Failed' || item.json['Classification_Code'] === 'ERROR')
  .map((item, index) => ({
    'Index': index + 1,
    'Element_Group': item.json['Type Name'] || item.json['Category'] || 'Unknown',
    'Category': item.json['Category'] || '',
    'Error': item.json['Error'] || 'Unknown error',
    'Element_Count': parseInt(item.json['Element Count']) || 0
  }));

// Sheet 6: Mapping Analysis (if mapping was used)
const mappingAnalysis = items
  .filter(item => item.json['Mapping_Used'] === 'Yes')
  .map((item, index) => ({
    'Index': index + 1,
    'Source_Category': item.json['Category'] || item.json['Type Name'] || 'Unknown',
    'Mapped_To_Code': item.json['Classification_Code'],
    'Mapped_To_Name': item.json['Classification_Name'],
    'Confidence': parseFloat(item.json['Classification_Confidence']) || 0,
    'Element_Count': parseInt(item.json['Element Count']) || 0
  }));

// Sheet 7: Low Confidence Items (confidence < 60%)
const lowConfidenceItems = items
  .filter(item => parseFloat(item.json['Classification_Confidence']) < 60)
  .sort((a, b) => parseFloat(a.json['Classification_Confidence']) - parseFloat(b.json['Classification_Confidence']))
  .map((item, index) => ({
    'Index': index + 1,
    'Element_Group': item.json['Type Name'] || item.json['Category'] || 'Unknown',
    'Category': item.json['Category'] || '',
    'Classification_Code': item.json['Classification_Code'],
    'Classification_Name': item.json['Classification_Name'],
    'Confidence_%': parseFloat(item.json['Classification_Confidence']) || 0,
    'Reasoning': item.json['Classification_Reasoning'] || '',
    'Alternative_Classes': item.json['Alternative_Classes'] || ''
  }));

// Create worksheet structure
const worksheets = [
  { name: 'Summary', data: executiveSummary },
  { name: 'Classification Summary', data: classificationSummary },
  { name: 'Detailed Results', data: detailedResults },
  { name: 'Element Parameters', data: elementParameters },
  { name: 'Failed Classifications', data: failedClassifications.length > 0 ? failedClassifications : [{ Message: 'No failed classifications' }] },
  { name: 'Mapping Analysis', data: mappingAnalysis.length > 0 ? mappingAnalysis : [{ Message: 'No mapping rules were used' }] },
  { name: 'Low Confidence', data: lowConfidenceItems.length > 0 ? lowConfidenceItems : [{ Message: 'No low confidence items' }] }
];

// Flatten all data with sheet markers for n8n spreadsheet node
const allData = [];
worksheets.forEach(sheet => {
  sheet.data.forEach(row => {
    allData.push({
      json: {
        ...row,
        _sheetName: sheet.name
      }
    });
  });
});

console.log(`Prepared ${allData.length} rows across ${worksheets.length} sheets`);

return allData;

// Similar logic for Excel file
const projectFile = $node['Setup - Define file paths1'].json.project_file;

// Extract directory path from project file
const path = projectFile.substring(0, projectFile.lastIndexOf('\\'));

// Create filename with timestamp
const timestamp = new Date().toISOString().slice(0,10);
const excelFileName = `CO2_Analysis_Professional_Report_${timestamp}.xlsx`;
const fullPath = `${path}\\${excelFileName}`;

console.log('Excel file will be saved to:', fullPath);

return [{
  json: {
    excel_filename: excelFileName,
    full_path: fullPath,
    directory: path,
    project_file: projectFile
  },
  binary: $input.first().binary
}];

// Generate professional classification report with volume analysis and model information
const items = $input.all();
const context = $getWorkflowStaticData('global').classificationSummary || {};
const projectFile = $node['Setup - Define file paths1'].json.project_file || '';
const classificationName = $node['Setup - Define file paths1'].json.classification_name || 'Unknown Classification';
const projectName = projectFile.split('\\').pop().replace(/\.(rvt|ifc|dwg|dgn)$/i, '').replace(/_/g, ' ');

// Get model name from global storage
const modelName = $getWorkflowStaticData('global').aiModelName || 'Unknown Model';

// Calculate statistics with volumes
const totalGroups = items.length;
const totalElements = items.reduce((sum, item) => sum + (parseInt(item.json['Element Count'] || item.json['Element_Count']) || 0), 0);
const totalBoundingBoxVolume = items.reduce((sum, item) => sum + (parseFloat(item.json['BoundingBox_Volume_Total']) || 0), 0);
const totalRegularVolume = items.reduce((sum, item) => sum + (parseFloat(item.json['Volume']) || 0), 0);

const classifiedGroups = items.filter(item => 
  item.json['Classification_Code'] !== 'ERROR' && 
  item.json['Classification_Code'] !== 'Unclassified'
).length;

const avgConfidence = items.reduce((sum, item) => 
  sum + (parseFloat(item.json['Classification_Confidence']) || 0), 0
) / items.length;

// Count mapping usage
const mappingUsed = items.filter(item => item.json['Mapping_Used'] === 'Yes').length;

// Count model usage for each item
const modelUsage = {};
items.forEach(item => {
  const model = item.json['Model_Used'] || 'Unknown';
  modelUsage[model] = (modelUsage[model] || 0) + 1;
});

// Group by classification with volumes
const byClassification = {};
items.forEach(item => {
  const code = item.json['Classification_Code'] || 'Unclassified';
  if (!byClassification[code]) {
    byClassification[code] = {
      name: item.json['Classification_Name'],
      count: 0,
      elements: 0,
      totalConfidence: 0,
      boundingBoxVolume: 0,
      regularVolume: 0,
      items: []
    };
  }
  byClassification[code].count++;
  byClassification[code].elements += parseInt(item.json['Element Count'] || item.json['Element_Count']) || 0;
  byClassification[code].totalConfidence += parseFloat(item.json['Classification_Confidence']) || 0;
  byClassification[code].boundingBoxVolume += parseFloat(item.json['BoundingBox_Volume_Total']) || 0;
  byClassification[code].regularVolume += parseFloat(item.json['Volume']) || 0;
  byClassification[code].items.push(item.json);
});

// Calculate average confidence per class
Object.keys(byClassification).forEach(code => {
  byClassification[code].avgConfidence = byClassification[code].totalConfidence / byClassification[code].count;
});

// Sort by volume (regular volume as primary sort)
const sortedClasses = Object.entries(byClassification)
  .sort((a, b) => b[1].regularVolume - a[1].regularVolume);

// Prepare multiple chart data
const chartLabels = sortedClasses.slice(0, 10).map(([code]) => code);
const chartData = sortedClasses.slice(0, 10).map(([, data]) => data.count);
const volumeChartData = sortedClasses.slice(0, 10).map(([, data]) => data.regularVolume.toFixed(2));
const elementCountChartData = sortedClasses.slice(0, 10).map(([, data]) => data.elements);

// Prepare pie charts data
const pieChartDataCount = sortedClasses.slice(0, 8).map(([code, data]) => ({
  label: `${code}: ${data.name}`,
  value: data.elements,
  percentage: ((data.elements / totalElements) * 100).toFixed(1)
}));

const pieChartDataVolume = sortedClasses.slice(0, 8).map(([code, data]) => ({
  label: `${code}: ${data.name}`,
  value: data.regularVolume.toFixed(2),
  percentage: totalRegularVolume > 0 ? ((data.regularVolume / totalRegularVolume) * 100).toFixed(1) : '0'
}));

// Generate recommendations based on analysis
const recommendations = [];
const lowConfidenceCount = items.filter(item => parseFloat(item.json['Classification_Confidence']) < 60).length;
const unclassifiedCount = items.filter(item => item.json['Classification_Code'] === 'Unclassified' || item.json['Classification_Code'] === 'ERROR').length;

if (lowConfidenceCount > 0) {
  recommendations.push({
    type: 'warning',
    icon: '‚ö†Ô∏è',
    text: `${lowConfidenceCount} elements have confidence below 60% - manual review recommended`,
    priority: 'high'
  });
}

if (unclassifiedCount > 0) {
  recommendations.push({
    type: 'error',
    icon: '‚ùå',
    text: `${unclassifiedCount} elements failed classification - check element data quality`,
    priority: 'high'
  });
}

if (mappingUsed < totalGroups * 0.3 && context.totalMappingRules > 0) {
  recommendations.push({
    type: 'info',
    icon: 'üí°',
    text: `Only ${((mappingUsed/totalGroups)*100).toFixed(1)}% of elements use mapping rules - consider expanding mapping file`,
    priority: 'medium'
  });
}

if (avgConfidence > 80) {
  recommendations.push({
    type: 'success',
    icon: '‚úÖ',
    text: `Classification confidence is excellent (${avgConfidence.toFixed(1)}%)`,
    priority: 'low'
  });
}

