    }
  }

  console.log(`Model for report: ${modelName}`);

  // Extract volume data from original data
  const boundingBoxVolume = parseFloat(originalData['BoundingBox_Volume_Total']) || 0;
  const regularVolume = parseFloat(originalData['Volume']) || 0;
  const elementCount = parseInt(originalData['Element Count']) || 0;

