// Universal Content Extractor with PDF support
const config = $input.first().json;
const binary = $input.first().binary;

console.log(`\n=== UNIVERSAL CONTENT EXTRACTOR ===`);
console.log(`Processing: ${config.fileInfo?.fileName || 'Unknown'}`);
console.log(`Type: ${config.processingType}`);

let extractedContent = '';
let classifications = {};
let structuredData = {
  type: config.processingType,
  metadata: {}
};

// Universal pattern extraction function
function extractClassificationsUniversal(text) {
  const classifs = {};
  if (!text) return classifs;

  // Comprehensive patterns for various classification formats
  const patterns = [
    // Standard code: description formats
    /^([A-Z0-9][A-Z0-9\-\._>]{0,20})\s*[:=\->|]+\s*(.+)$/gim,
    // Numeric codes with descriptions
    /^(\d{1,10})\s+([^\d\n\r].{2,500})$/gm,
    // Hierarchical dotted notation
    /^(\d{1,3}(?:\.\d{1,3}){0,5})\s+(.+)$/gm,
    // Alphanumeric codes
    /^([A-Z]{1,5}[\-\.]?\d{1,5}[A-Z0-9]*)\s*[:=\-]?\s*(.+)$/gim,
    // Table/grid format with pipes
    /\|?\s*([A-Z0-9][A-Z0-9\-\.>]{0,20})\s*\|?\s*([^|\n]{2,500})\s*\|?/gi,
    // XML/HTML style
    /<(?:code|id|key)>([^<]+)<\/(?:code|id|key)>.*?<(?:name|value|description)>([^<]+)<\/(?:name|value|description)>/gi,
    // JSON style
    /"([A-Z0-9][A-Z0-9\-\.]{0,20})"\s*:\s*[\[{]?"([^"]{2,500})"/gi,
    // Special format with arrows or greater than
    /([A-Z0-9]+>?[A-Z0-9]*)\s*\|?\s*([^|\n]+)/g,
    // CoClass format
    /(CIM>[A-Z]{1,4})\s*[-‚Äì]\s*([^,\n]+)/gi,
    // Omniclass format
    /(\d{2}-\d{2}\s\d{2}\s\d{2})\s*[-‚Äì]\s*([^,\n]+)/gi,
    // Uniclass format
    /([A-Z]{2}_\d{2}_\d{2}_\d{2})\s*[-‚Äì]\s*([^,\n]+)/gi,
    // MasterFormat
    /(\d{2}\s\d{2}\s\d{2})\s*[-‚Äì]\s*([^,\n]+)/gi
  ];

  // Extract using all patterns
  patterns.forEach(pattern => {
    let match;
    const regex = new RegExp(pattern);
    while ((match = regex.exec(text)) !== null) {
      const code = match[1]?.trim();
      const desc = match[2]?.trim();

      // Validate extracted data
      if (code && desc && 
          code.length >= 1 && code.length <= 50 &&
          desc.length >= 2 && desc.length <= 1000 &&
          !code.match(/^(and|or|the|with|from|this|that|for|are|was|were)$/i)) {

        // Clean the description
        const cleanDesc = desc
          .replace(/\s+/g, ' ')
          .replace(/^[\s\-\.,;:]+|[\s\-\.,;:]+$/g, '')
          .replace(/\.$/, '');

        if (cleanDesc && cleanDesc.length > 1) {
          classifs[code] = cleanDesc;
        }
      }
    }
  });

  return classifs;
}

// Enhanced PDF extraction function
function extractFromPDF(buffer) {
  const pdfContent = [];
  const pdfText = buffer.toString('latin1');

  // Method 1: Extract text from PDF streams
  const streamPattern = /stream([\s\S]*?)endstream/g;
  let streamMatch;
  while ((streamMatch = streamPattern.exec(pdfText)) !== null) {
    const streamContent = streamMatch[1];

    // Try to decompress FlateDecode streams (most common in PDFs)
    try {
      // Look for text in brackets (common PDF text format)
      const textInBrackets = streamContent.match(/\(([^)]+)\)/g) || [];
      textInBrackets.forEach(match => {
        let text = match.slice(1, -1);
        // Decode PDF escape sequences
        text = text
          .replace(/\\n/g, '\n')
          .replace(/\\r/g, '\r')
          .replace(/\\t/g, '\t')
          .replace(/\\\(/g, '(')
          .replace(/\\\)/g, ')')
          .replace(/\\\\/g, '\\')
          .replace(/\\(\d{3})/g, (m, octal) => String.fromCharCode(parseInt(octal, 8)));

        if (text.trim().length > 1) {
          pdfContent.push(text.trim());
        }
      });

      // Look for Tj/TJ operators (text showing operators)
      const tjPattern = /\[(.*?)\]\s*TJ/g;
      let tjMatch;
      while ((tjMatch = tjPattern.exec(streamContent)) !== null) {
        const textArray = tjMatch[1];
        const texts = textArray.match(/\(([^)]*)\)/g) || [];
        texts.forEach(t => {
          const cleaned = t.slice(1, -1).trim();
          if (cleaned.length > 0) {
            pdfContent.push(cleaned);
          }
        });
      }

      // Simple Tj operator
      const simpleTjPattern = /\(([^)]+)\)\s*Tj/g;
      let simpleTjMatch;
      while ((simpleTjMatch = simpleTjPattern.exec(streamContent)) !== null) {
        const text = simpleTjMatch[1].trim();
        if (text.length > 0) {
          pdfContent.push(text);
        }
      }
    } catch (e) {
      // Continue if decompression fails
    }
  }

  // Method 2: Look for BT...ET blocks (text objects)
  const textObjectPattern = /BT([\s\S]*?)ET/g;
  let textMatch;
  while ((textMatch = textObjectPattern.exec(pdfText)) !== null) {
    const textBlock = textMatch[1];

    // Extract text from this block
    const texts = textBlock.match(/\(([^)]+)\)/g) || [];
    texts.forEach(match => {
      let text = match.slice(1, -1);
      // Basic PDF escape sequence handling
      text = text
        .replace(/\\n/g, ' ')
        .replace(/\\r/g, ' ')
        .replace(/\\\(/g, '(')
        .replace(/\\\)/g, ')')
        .trim();

      if (text.length > 1 && !text.match(/^[\d\s\.\-]+$/)) {
        pdfContent.push(text);
      }
    });
  }

  // Method 3: Extract from object definitions
  const objPattern = /obj([\s\S]*?)endobj/g;
  let objMatch;
  while ((objMatch = objPattern.exec(pdfText)) !== null) {
    const objContent = objMatch[1];

    // Look for dictionaries with text
    if (objContent.includes('/Type') || objContent.includes('/Contents')) {
      const texts = objContent.match(/\(([^)]+)\)/g) || [];
      texts.forEach(match => {
        const text = match.slice(1, -1).trim();
        if (text.length > 2 && !text.match(/^[\d\s]+$/)) {
          pdfContent.push(text);
        }
      });
    }
  }

  // Method 4: Look for Unicode strings
  const unicodePattern = /<([0-9A-Fa-f]+)>/g;
  let unicodeMatch;
  while ((unicodeMatch = unicodePattern.exec(pdfText)) !== null) {
    try {
      const hex = unicodeMatch[1];
      let text = '';
      for (let i = 0; i < hex.length; i += 4) {
        const charCode = parseInt(hex.substr(i, 4), 16);
        if (charCode > 31 && charCode < 65536) {
          text += String.fromCharCode(charCode);
        }
      }
      if (text.trim().length > 1) {
        pdfContent.push(text.trim());
      }
    } catch (e) {
      // Continue on error
    }
  }

  // Combine and clean extracted content
  let combinedText = pdfContent.join('\n');

  // Remove duplicate lines
  const lines = combinedText.split('\n');
  const uniqueLines = [...new Set(lines)];
  combinedText = uniqueLines.join('\n');

  // Clean up common PDF artifacts
  combinedText = combinedText
    .replace(/\0/g, '')
    .replace(/[\x00-\x1F\x7F-\x9F]/g, ' ')
    .replace(/\s+/g, ' ')
    .replace(/(\w)\s+(\w)/g, '$1 $2');

  return combinedText;
}

// Main processing logic
if (config.requiresSpreadsheetProcessing) {
  console.log('Spreadsheet file detected - will attempt multiple extraction methods');

  // Try to extract as binary/text first as fallback
  if (binary?.data) {
    const buffer = Buffer.from(binary.data.data, 'base64');
    const textChunks = [];
    let current = '';

    // Extract readable strings from binary
    for (let i = 0; i < buffer.length; i++) {
      const byte = buffer[i];
      if ((byte >= 32 && byte <= 126) || (byte >= 160 && byte <= 255)) {
        current += String.fromCharCode(byte);
      } else {
        if (current.length > 3) {
          textChunks.push(current.trim());
        }
        current = '';
      }
    }

    extractedContent = textChunks.join(' | ');
    classifications = extractClassificationsUniversal(extractedContent);

    console.log(`Fallback extraction from binary: ${Object.keys(classifications).length} classifications`);
  }

  // Mark for specialized processing
  config.needsSpreadsheetParser = true;

} else if (config.requiresDocumentProcessing) {
  console.log('Document processing required');

  if (binary?.data) {
    const buffer = Buffer.from(binary.data.data, 'base64');

    if (config.fileInfo.extension === 'pdf') {
      console.log('PDF detected - using specialized PDF extraction');

      // Use enhanced PDF extraction
      extractedContent = extractFromPDF(buffer);

      // If PDF extraction didn't work well, try alternative method
      if (extractedContent.length < 100) {
        console.log('PDF extraction yielded little content, trying alternative methods...');

        // Alternative: Extract all printable ASCII sequences
        const asciiChunks = [];
        let currentChunk = '';

        for (let i = 0; i < buffer.length; i++) {
          const byte = buffer[i];
          if (byte >= 32 && byte <= 126) {
            currentChunk += String.fromCharCode(byte);
          } else {
            if (currentChunk.length > 10) {
              // Only keep chunks that look like text
              if (!currentChunk.match(/^[\d\s\W]+$/) && currentChunk.match(/[a-zA-Z]/)) {
                asciiChunks.push(currentChunk.trim());
              }
            }
            currentChunk = '';
          }
        }

        const alternativeContent = asciiChunks.join('\n');
        if (alternativeContent.length > extractedContent.length) {
          extractedContent = alternativeContent;
          console.log('Alternative extraction yielded more content');
        }
      }

    } else {
      // For Word documents and other binary formats
      console.log(`Processing ${config.fileInfo.extension} document`);

      const textChunks = [];
      let current = '';

      // Extract all readable strings
      for (let i = 0; i < buffer.length; i++) {
        const byte = buffer[i];

        // Check for printable characters (including extended ASCII)
        if ((byte >= 32 && byte <= 126) || (byte >= 160 && byte <= 255)) {
          current += String.fromCharCode(byte);
        } else {
          if (current.length > 2) {
            textChunks.push(current.trim());
          }
          current = '';
        }
      }

      // Also try UTF-16 LE for Word docs
      try {
        const utf16Text = buffer.toString('utf16le');
        if (utf16Text && !utf16Text.includes(' ')) {
          const cleanUtf16 = utf16Text.replace(/[\x00-\x1F\x7F-\x9F]/g, ' ').trim();
          if (cleanUtf16.length > 100) {
            textChunks.push(cleanUtf16);
          }
        }
      } catch (e) {
        // Ignore UTF-16 errors
      }

      extractedContent = textChunks.join('\n');
    }
  }

  classifications = extractClassificationsUniversal(extractedContent);

} else if (config.requiresDelimitedProcessing) {
  // Handle CSV/TSV files
  if (binary?.data) {
    const buffer = Buffer.from(binary.data.data, 'base64');
    extractedContent = buffer.toString('utf8');

    // Parse delimited data
    const delimiter = config.fileInfo.extension === 'tsv' ? '\t' : ',';
    const lines = extractedContent.split(/\r?\n/);

    if (lines.length > 0) {
      const headers = lines[0].split(delimiter).map(h => h.trim().replace(/^"|"$/g, ''));

      for (let i = 1; i < lines.length; i++) {
        const values = lines[i].split(delimiter).map(v => v.trim().replace(/^"|"$/g, ''));

        // Try to find code-description pairs
        if (values.length >= 2) {
          const potentialCode = values[0];
          const potentialDesc = values[1];

          if (potentialCode && potentialDesc && 
              potentialCode.length <= 50 && 
              potentialDesc.length > 1) {
            classifications[potentialCode] = potentialDesc;
          }
        }
      }
    }
  }

} else {
  // Text-based files or fallback
  if (binary?.data) {
    const buffer = Buffer.from(binary.data.data, 'base64');

    // Try multiple encodings
    const encodings = ['utf8', 'latin1', 'utf16le'];
    for (const encoding of encodings) {
      try {
        const decoded = buffer.toString(encoding);
        if (decoded && !decoded.includes(' ')) {
          extractedContent = decoded;
          break;
        }
      } catch (e) {
        continue;
      }
    }
  }

  classifications = extractClassificationsUniversal(extractedContent);
}

console.log(`Extracted text: ${extractedContent.length} chars`);
console.log(`Classifications found: ${Object.keys(classifications).length}`);

if (Object.keys(classifications).length > 0) {
  console.log(`Sample classifications:`);
  Object.entries(classifications).slice(0, 5).forEach(([code, desc]) => {
    console.log(`  ${code}: ${desc.substring(0, 60)}...`);
  });
} else if (extractedContent.length > 0) {
  console.log('No classifications found, but text was extracted.');
  console.log('Sample of extracted text:');
  console.log(extractedContent.substring(0, 500));
}

console.log(`====================================\n`);

// Prepare output
config.extractedContent = {
  text: extractedContent,
  classifications: classifications,
  totalClassifications: Object.keys(classifications).length,
  confidence: Object.keys(classifications).length > 10 ? 85 : 60,
  extractionMethod: config.fileInfo?.extension === 'pdf' ? 'PDF extraction' : 'Binary extraction'
};

return [{ json: config, binary: binary }];

// Simplified Spreadsheet Parser
const config = $node['Universal Extractor Enhanced'].json;
const parseAttempt = $input.all();

console.log(`\n=== SIMPLIFIED SPREADSHEET PARSER ===`);

let classifications = {};
let fullContent = ''; // This will store the text representation of the table
let structuredData = {
  parseMethod: 'unknown',
  totalRows: 0,
};

// Check if the standard spreadsheet parser was successful
if (parseAttempt.length > 0 && parseAttempt[0].json && !parseAttempt[0].json.error) {
  console.log(`Standard parse successful: ${parseAttempt.length} rows`);
  structuredData.parseMethod = 'standard';
  structuredData.totalRows = parseAttempt.length;

  // Convert each row to a JSON string and append it.
  // WARNING: This can create a very large text block for the LLM,
  // which will be slower and more expensive to process.
  const allRowsAsText = parseAttempt.map(item => JSON.stringify(item.json)).join('\n');

  fullContent = allRowsAsText;

  // For compatibility, we'll try to populate 'classifications' from the first two columns.
  // This is a basic fallback.
  if (parseAttempt.length > 0) {
    const firstItemKeys = Object.keys(parseAttempt[0].json);
    if (firstItemKeys.length >= 2) {
      parseAttempt.forEach(item => {
        const row = item.json;
        const key = row[firstItemKeys[0]];
        const value = row[firstItemKeys[1]];
        if (key && value) {
          classifications[String(key)] = String(value);
        }
      });
    }
  }

} else {
  // If parsing failed, use the fallback data from the previous node
  console.log('Standard parse failed. Using fallback from binary extraction.');
  structuredData.parseMethod = 'binary_fallback';
  classifications = config.extractedContent?.classifications || {};
  fullContent = config.extractedContent?.text || '';
}

console.log(`\nExtraction complete:`);
console.log(`- Parse method: ${structuredData.parseMethod}`);
console.log(`- Total rows processed: ${structuredData.totalRows}`);
console.log(`- Total classifications found (basic): ${Object.keys(classifications).length}`);
console.log(`- Full content length for LLM: ${fullContent.length} chars`);
console.log(`=====================================\n`);

config.extractedContent = {
  text: fullContent,
  classifications: classifications, // Basic classifications from first 2 columns
  totalClassifications: Object.keys(classifications).length,
  confidence: 70, // Confidence is lower as we are not intelligently parsing
  source: 'Spreadsheet',
  parseMethod: structuredData.parseMethod,
};

return [{ json: config }];

// Direct Classification Mode - No mapping file provided
