console.log(`No mapping file provided`);
console.log(`Classification system: ${config.classificationName || 'Will be determined by AI'}`);
console.log(`Using AI for direct classification without predefined mappings`);

const directConfig = {
  ...config,
  mode: 'direct',
  mappingRules: {},
  totalMappingRules: 0,
  uniqueCodes: 0,
  mappingFound: false,
  confidence: 60,
  extractionComplete: true,
  classificationSystem: config.classificationName || 'Unknown',
  systemType: 'AI-determined'
