// ÐÐ°Ñ…Ð¾Ð´Ð¸Ð¼ Ð¿Ð¾Ð»Ñ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¹ Ð´Ð»Ñ Ð°Ð½Ð°Ð»Ð¸Ð·Ð°
const items = $input.all();
if (items.length === 0) {
  return [{json: {error: 'No grouped data found'}}];
}

// ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð²ÑÐµ Ð·Ð°Ð³Ð¾Ð»Ð¾Ð²ÐºÐ¸ Ð¸Ð· Ð¿ÐµÑ€Ð²Ð¾Ð³Ð¾ ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ð°
const headers = Object.keys(items[0].json);

// Ð˜Ñ‰ÐµÐ¼ Ð¿Ð¾Ð»Ñ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¹ Ð¿Ð¾ Ð¿Ñ€Ð¸Ð¾Ñ€Ð¸Ñ‚ÐµÑ‚Ñƒ
const categoryPatterns = [
  { pattern: /^category$/i, type: 'Category' },
  { pattern: /^ifc[\s_-]?type$/i, type: 'IFC' },
  { pattern: /^host[\s_-]?category$/i, type: 'Host' },
  { pattern: /^ifc[\s_-]?export[\s_-]?as$/i, type: 'Export' },
  { pattern: /^layer$/i, type: 'Layer' }
];

let categoryField = null;
let categoryFieldType = 'None';

// Ð˜Ñ‰ÐµÐ¼ Ð¿Ð¾Ð»Ðµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸
for (const header of headers) {
  for (const {pattern, type} of categoryPatterns) {
    if (pattern.test(header)) {
      categoryField = header;
      categoryFieldType = type;
      break;
    }
  }
  if (categoryField) break;
}

// ÐÐ°Ñ…Ð¾Ð´Ð¸Ð¼ volumetric Ð¿Ð¾Ð»Ñ
const volumetricPatterns = /volume|area|length|count|quantity|thickness|perimeter|depth|size|dimension|weight|mass/i;
const volumetricFields = headers.filter(header => volumetricPatterns.test(header));

// Ð¡Ð¾Ð±Ð¸Ñ€Ð°ÐµÐ¼ ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ðµ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ñ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¹ ÐµÑÐ»Ð¸ Ð¿Ð¾Ð»Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½Ð¾
const categoryValues = new Set();
if (categoryField) {
  items.forEach(item => {
    const value = item.json[categoryField];
    if (value && value !== '' && value !== null) {
      categoryValues.add(value);
    }
  });
}

console.log('Category field analysis:');
console.log('- Field found:', categoryField || 'None');
console.log('- Field type:', categoryFieldType);
console.log('- Unique values:', categoryValues.size);
console.log('- Volumetric fields:', volumetricFields.length);

// ÐŸÐ¾Ð´Ð³Ð¾Ñ‚Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð´Ð»Ñ AI ÐºÐ»Ð°ÑÑÐ¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸Ð¸
return [{
  json: {
    categoryField: categoryField,
    categoryFieldType: categoryFieldType,
    categoryValues: Array.from(categoryValues),
    volumetricFields: volumetricFields,
    groupedData: items.map(item => item.json),
    totalGroups: items.length
  }
}];

// ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ ÐºÐ»Ð°ÑÑÐ¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸ÑŽ Ð¾Ñ‚ AI Ð¸ Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ðº ÑÐ³Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ð¼ Ð´Ð°Ð½Ð½Ñ‹Ð¼
const categoryInfo = $node['Find Category Fields1'].json;
const groupedData = categoryInfo.groupedData;
const categoryField = categoryInfo.categoryField;
const volumetricFields = categoryInfo.volumetricFields || [];

// Ð˜Ð·Ð²Ð»ÐµÐºÐ°ÐµÐ¼ ÐºÐ»Ð°ÑÑÐ¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸ÑŽ Ð¸Ð· AI Ð¾Ñ‚Ð²ÐµÑ‚Ð°
let classifications = {};
let buildingCategories = [];
let drawingCategories = [];

try {
  const aiResponse = $input.first().json;
  const content = aiResponse.content || aiResponse.message || aiResponse.response || '';

  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    const parsed = JSON.parse(jsonMatch[0]);
    classifications = parsed.classifications || {};
    buildingCategories = parsed.building_categories || [];
    drawingCategories = parsed.drawing_categories || [];
    console.log(`AI classified ${Object.keys(classifications).length} categories`);
    console.log(`Building categories: ${buildingCategories.length}`);
    console.log(`Drawing categories: ${drawingCategories.length}`);
  }
} catch (error) {
  console.error('Error parsing AI classification:', error.message);
}

// ÐŸÑ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ ÐºÐ»Ð°ÑÑÐ¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸ÑŽ Ðº ÐºÐ°Ð¶Ð´Ð¾Ð¹ Ð³Ñ€ÑƒÐ¿Ð¿Ðµ
return groupedData.map(group => {
  let isBuildingElement = false;
  let reason = '';
  let confidence = 0;

  if (categoryField && group[categoryField]) {
    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ ÐºÐ»Ð°ÑÑÐ¸Ñ„Ð¸ÐºÐ°Ñ†Ð¸ÑŽ Ð¿Ð¾ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸
    const categoryValue = group[categoryField];

    if (classifications[categoryValue] !== undefined) {
      isBuildingElement = classifications[categoryValue];
      confidence = 95;
      reason = `Category '${categoryValue}' classified by AI as ${isBuildingElement ? 'building element' : 'drawing/annotation'}`;
    } else {
      // Fallback: Ð¿Ñ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð¿Ð¾ ÐºÐ»ÑŽÑ‡ÐµÐ²Ñ‹Ð¼ ÑÐ»Ð¾Ð²Ð°Ð¼
      const lowerCategory = categoryValue.toLowerCase();
      const drawingKeywords = /annotation|drawing|text|dimension|tag|view|sheet|grid|section|elevation|callout|revision|legend|symbol|mark|note|detail items|filled region|detail line/i;
      const buildingKeywords = /wall|floor|roof|column|beam|door|window|stair|pipe|duct|equipment|fixture|furniture/i;

      if (drawingKeywords.test(lowerCategory)) {
        isBuildingElement = false;
        confidence = 85;
        reason = `Category '${categoryValue}' matched drawing keywords`;
      } else if (buildingKeywords.test(lowerCategory)) {
        isBuildingElement = true;
        confidence = 85;
        reason = `Category '${categoryValue}' matched building keywords`;
      } else {
        // ÐŸÐ¾ ÑƒÐ¼Ð¾Ð»Ñ‡Ð°Ð½Ð¸ÑŽ ÑÑ‡Ð¸Ñ‚Ð°ÐµÐ¼ ÑÑ‚Ñ€Ð¾Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ð¼ ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ð¾Ð¼ ÐµÑÐ»Ð¸ Ð½Ðµ Ð¾Ñ‡ÐµÐ²Ð¸Ð´Ð½Ð¾ Ð¾Ð±Ñ€Ð°Ñ‚Ð½Ð¾Ðµ
        isBuildingElement = true;
        confidence = 70;
        reason = `Category '${categoryValue}' assumed as building element (no clear match)`;
      }
    }
  } else {
    // Ð•ÑÐ»Ð¸ Ð½ÐµÑ‚ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸, Ð¿Ñ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð½Ð°Ð»Ð¸Ñ‡Ð¸Ðµ Ð¾Ð±ÑŠÐµÐ¼Ð½Ñ‹Ñ… Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ð¾Ð²
    let hasSignificantVolumetricData = false;
    let volumetricCount = 0;

    for (const field of volumetricFields) {
      const value = parseFloat(group[field]);
      if (!isNaN(value) && value > 0) {
        hasSignificantVolumetricData = true;
        volumetricCount++;
      }
    }

    isBuildingElement = hasSignificantVolumetricData;
    confidence = hasSignificantVolumetricData ? 80 : 60;
    reason = hasSignificantVolumetricData ? 
      `Has ${volumetricCount} volumetric parameters with values` : 
      'No category field and no significant volumetric data';
  }

  return {
    json: {
      ...group,
      is_building_element: isBuildingElement,
      element_confidence: confidence,
      element_reason: reason
    }
  };
});

// Ð˜Ð·Ð²Ð»ÐµÐºÐ°ÐµÐ¼ Ð¸ Ð¾Ñ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ð·Ð°Ð³Ð¾Ð»Ð¾Ð²ÐºÐ¸
 const items = $input.all();
 if (items.length === 0) {
  throw new Error('No data found in Excel file');
 }

// Ð¡Ð¾Ð±Ð¸Ñ€Ð°ÐµÐ¼ Ð²ÑÐµ ÑƒÐ½Ð¸ÐºÐ°Ð»ÑŒÐ½Ñ‹Ðµ Ð·Ð°Ð³Ð¾Ð»Ð¾Ð²ÐºÐ¸
 const allHeaders = new Set();
 items.forEach(item => {
  Object.keys(item.json).forEach(key => allHeaders.add(key));
 });

// ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ð·Ð°Ð³Ð¾Ð»Ð¾Ð²ÐºÐ¸ Ð¾Ñ‚ Ñ‚Ð¸Ð¿Ð¾Ð² Ð´Ð°Ð½Ð½Ñ‹Ñ…
 const headers = Array.from(allHeaders);
 const cleanedHeaders = headers.map(header => {
  return header.replace(/:\s*(string|double|int|float|boolean|number)\s*$/i, '').trim();
 });

// Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ð¼Ð°Ð¿Ð¿Ð¸Ð½Ð³ ÑÑ‚Ð°Ñ€Ñ‹Ñ… Ð·Ð°Ð³Ð¾Ð»Ð¾Ð²ÐºÐ¾Ð² Ðº Ð½Ð¾Ð²Ñ‹Ð¼
 const headerMapping = {};
 headers.forEach((oldHeader, index) => {
  headerMapping[oldHeader] = cleanedHeaders[index];
 });

// ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð¿Ñ€Ð¸Ð¼ÐµÑ€Ñ‹ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ð¹
 const sampleValues = {};
 cleanedHeaders.forEach((header, index) => {
  const originalHeader = headers[index];
  for (const item of items) {
    const value = item.json[originalHeader];
    if (value !== null && value !== undefined && value !== '') {
      sampleValues[header] = value;
      break;
    }
  }
  if (!sampleValues[header]) {
    sampleValues[header] = null;
  }
 });

 console.log(`Found ${headers.length} unique headers across ${items.length} items`);

// Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‰Ð°ÐµÐ¼ Ð²ÑÑŽ Ð½ÐµÐ¾Ð±Ñ…Ð¾Ð´Ð¸Ð¼ÑƒÑŽ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸ÑŽ
 return [{
  json: {
    headers: cleanedHeaders,
    originalHeaders: headers,
    headerMapping: headerMapping,
    sampleValues: sampleValues,
    totalRows: items.length,
    totalHeaders: headers.length,
    // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð²ÑÐµ Ð¸ÑÑ…Ð¾Ð´Ð½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ
    rawData: items.map(item => item.json)
  }
 }];

// ÐžÐ±Ñ€Ð°Ð±Ð°Ñ‚Ñ‹Ð²Ð°ÐµÐ¼ Ð¾Ñ‚Ð²ÐµÑ‚ AI Ð¸ Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ð¿Ñ€Ð°Ð²Ð¸Ð»Ð°
 const aiResponse = $input.first().json;
 const headerData = $node['Extract Headers and Data1'].json;

// Ð˜Ð·Ð²Ð»ÐµÐºÐ°ÐµÐ¼ Ð¿Ñ€Ð°Ð²Ð¸Ð»Ð° Ð¸Ð· AI Ð¾Ñ‚Ð²ÐµÑ‚Ð°
 let aiRules = {};
 try {
  const content = aiResponse.content || aiResponse.message || aiResponse.response || '';
  console.log('AI Response received, length:', content.length);

  // Ð˜Ñ‰ÐµÐ¼ JSON Ð² Ð¾Ñ‚Ð²ÐµÑ‚Ðµ
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    const parsed = JSON.parse(jsonMatch[0]);
    aiRules = parsed.aggregation_rules || parsed.parameter_aggregation || {};
    console.log(`AI provided ${Object.keys(aiRules).length} rules`);
  } else {
    console.warn('No JSON found in AI response');
  }
 } catch (error) {
  console.error('Error parsing AI response:', error.message);
 }

// Ð¡Ð¾Ð·Ð´Ð°ÐµÐ¼ Ñ„Ð¸Ð½Ð°Ð»ÑŒÐ½Ñ‹Ðµ Ð¿Ñ€Ð°Ð²Ð¸Ð»Ð° Ñ Ð´ÐµÑ„Ð¾Ð»Ñ‚Ð°Ð¼Ð¸
 const finalRules = {};
 headerData.headers.forEach(header => {
  if (aiRules[header]) {
    finalRules[header] = aiRules[header];
  } else {
    // ÐŸÑ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ð¿Ñ€Ð°Ð²Ð¸Ð»Ð° Ð¿Ð¾ ÑƒÐ¼Ð¾Ð»Ñ‡Ð°Ð½Ð¸ÑŽ
    const lowerHeader = header.toLowerCase();

    if (lowerHeader.match(/volume|area|length|width|height|count|quantity|thickness|perimeter|depth|size|dimension|weight|mass|total|amount|number/)) {
      finalRules[header] = 'sum';
    } else if (lowerHeader.match(/price|rate|cost|coefficient|factor|percent|ratio|efficiency|avg|average|mean/)) {
      finalRules[header] = 'mean';
    } else {
      finalRules[header] = 'first';
    }
  }
 });

 const groupByParam = $node['Setup - Define file paths'].json.group_by;

 console.log(`\nAggregation rules summary:`);
 console.log(`- Total headers: ${headerData.headers.length}`);
 console.log(`- AI rules: ${Object.keys(aiRules).length}`);
 console.log(`- Default rules: ${headerData.headers.length - Object.keys(aiRules).length}`);
 console.log(`- Group by: ${groupByParam}`);

// Ð’Ð¾Ð·Ð²Ñ€Ð°Ñ‰Ð°ÐµÐ¼ Ð²ÑÐµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð´Ð»Ñ Ð³Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€Ð¾Ð²ÐºÐ¸
 return [{
  json: {
    aggregationRules: finalRules,
    headerMapping: headerData.headerMapping,
    headers: headerData.headers,
    originalHeaders: headerData.originalHeaders,
    rawData: headerData.rawData,
    groupByParam: groupByParam,
    totalRows: headerData.totalRows
  }
 }];

// Ð’Ñ‹Ð¿Ð¾Ð»Ð½ÑÐµÐ¼ Ð³Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€Ð¾Ð²ÐºÑƒ Ð´Ð°Ð½Ð½Ñ‹Ñ…
 const input = $input.first().json;
 const aggregationRules = input.aggregationRules;
 const headerMapping = input.headerMapping;
 const rawData = input.rawData;
 const groupByParamOriginal = input.groupByParam;

// ÐÐ°Ñ…Ð¾Ð´Ð¸Ð¼ Ð¾Ñ‡Ð¸Ñ‰ÐµÐ½Ð½Ð¾Ðµ Ð¸Ð¼Ñ Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ð° Ð³Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€Ð¾Ð²ÐºÐ¸
 const groupByParam = headerMapping[groupByParamOriginal] || groupByParamOriginal;

 console.log(`Grouping ${rawData.length} items by: ${groupByParam}`);

// ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ - Ð¿ÐµÑ€ÐµÐ¸Ð¼ÐµÐ½Ð¾Ð²Ñ‹Ð²Ð°ÐµÐ¼ ÐºÐ¾Ð»Ð¾Ð½ÐºÐ¸
 const cleanedData = rawData.map(item => {
  const cleaned = {};
  Object.entries(item).forEach(([key, value]) => {
    const newKey = headerMapping[key] || key;
    cleaned[newKey] = value;
  });
  return cleaned;
 });

// Ð“Ñ€ÑƒÐ¿Ð¿Ð¸Ñ€ÑƒÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ
 const grouped = {};

// ÐŸÐµÑ€Ð²Ñ‹Ð¹ Ð¿Ñ€Ð¾Ñ…Ð¾Ð´ - ÑÐ¾Ð±Ð¸Ñ€Ð°ÐµÐ¼ Ð²ÑÐµ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ñ
 cleanedData.forEach(item => {
  const groupKey = item[groupByParam];

  if (!groupKey || groupKey === '' || groupKey === null) return;

  if (!grouped[groupKey]) {
    grouped[groupKey] = {
      _count: 0,
      _values: {}
    };

    // Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Ð¼Ð°ÑÑÐ¸Ð²Ñ‹ Ð´Ð»Ñ Ð²ÑÐµÑ… Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ð¾Ð²
    Object.keys(aggregationRules).forEach(param => {
      if (param !== groupByParam) {
        grouped[groupKey]._values[param] = [];
      }
    });
  }

  grouped[groupKey]._count++;

  // Ð¡Ð¾Ð±Ð¸Ñ€Ð°ÐµÐ¼ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ñ
  Object.entries(item).forEach(([key, value]) => {
    if (key === groupByParam) return;

    if (value !== null && value !== undefined && value !== '' && grouped[groupKey]._values[key]) {
      grouped[groupKey]._values[key].push(value);
    }
  });
 });

// Ð’Ñ‚Ð¾Ñ€Ð¾Ð¹ Ð¿Ñ€Ð¾Ñ…Ð¾Ð´ - Ð¿Ñ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð°Ð³Ñ€ÐµÐ³Ð°Ñ†Ð¸Ð¸
 const result = [];

 Object.entries(grouped).forEach(([groupKey, groupData]) => {
  const aggregated = {
    [groupByParam]: groupKey,
    'Element Count': groupData._count
  };

  // ÐŸÑ€Ð¸Ð¼ÐµÐ½ÑÐµÐ¼ Ð°Ð³Ñ€ÐµÐ³Ð°Ñ†Ð¸ÑŽ Ð´Ð»Ñ ÐºÐ°Ð¶Ð´Ð¾Ð³Ð¾ Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ð°
  Object.entries(groupData._values).forEach(([param, values]) => {
    const rule = aggregationRules[param] || 'first';

    if (values.length === 0) {
      aggregated[param] = null;
      return;
    }

    switch(rule) {
      case 'sum':
        const numericValues = values.map(v => {
          const num = parseFloat(String(v).replace(',', '.'));
          return isNaN(num) ? 0 : num;
        });
        aggregated[param] = numericValues.reduce((a, b) => a + b, 0);
        // ÐžÐºÑ€ÑƒÐ³Ð»ÑÐµÐ¼ Ð´Ð¾ 2 Ð·Ð½Ð°ÐºÐ¾Ð² Ð¿Ð¾ÑÐ»Ðµ Ð·Ð°Ð¿ÑÑ‚Ð¾Ð¹ ÐµÑÐ»Ð¸ Ð½ÑƒÐ¶Ð½Ð¾
        if (aggregated[param] % 1 !== 0) {
          aggregated[param] = Math.round(aggregated[param] * 100) / 100;
        }
        break;

      case 'mean':
      case 'average':
        const avgValues = values.map(v => {
          const num = parseFloat(String(v).replace(',', '.'));
          return isNaN(num) ? null : num;
        }).filter(v => v !== null);

        if (avgValues.length > 0) {
          const avg = avgValues.reduce((a, b) => a + b, 0) / avgValues.length;
          aggregated[param] = Math.round(avg * 100) / 100;
        } else {
          aggregated[param] = values[0];
        }
        break;

      case 'first':
      default:
        aggregated[param] = values[0];
        break;
    }
  });

  result.push({ json: aggregated });
 });

// Ð¡Ð¾Ñ€Ñ‚Ð¸Ñ€ÑƒÐµÐ¼ Ñ€ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚
 result.sort((a, b) => {
  const aVal = a.json[groupByParam];
  const bVal = b.json[groupByParam];
  if (aVal < bVal) return -1;
  if (aVal > bVal) return 1;
  return 0;
 });

 console.log(`\nGrouping complete:`);
 console.log(`- Input items: ${cleanedData.length}`);
 console.log(`- Output groups: ${result.length}`);
 console.log(`- Parameters processed: ${Object.keys(aggregationRules).length}`);

// Ð’Ñ‹Ð²Ð¾Ð´Ð¸Ð¼ Ð¿Ñ€Ð¸Ð¼ÐµÑ€Ñ‹ Ð°Ð³Ñ€ÐµÐ³Ð°Ñ†Ð¸Ð¸
 const rulesSummary = { sum: [], mean: [], first: [] };
 Object.entries(aggregationRules).forEach(([param, rule]) => {
  if (rulesSummary[rule]) rulesSummary[rule].push(param);
 });

 console.log('\nAggregation summary:');
 if (rulesSummary.sum.length > 0) {
  console.log(`- SUM (${rulesSummary.sum.length}): ${rulesSummary.sum.slice(0, 5).join(', ')}${rulesSummary.sum.length > 5 ? '...' : ''}`);
 }
 if (rulesSummary.mean.length > 0) {
  console.log(`- MEAN (${rulesSummary.mean.length}): ${rulesSummary.mean.slice(0, 5).join(', ')}${rulesSummary.mean.length > 5 ? '...' : ''}`);
 }
 if (rulesSummary.first.length > 0) {
  console.log(`- FIRST (${rulesSummary.first.length}): ${rulesSummary.first.slice(0, 5).join(', ')}${rulesSummary.first.length > 5 ? '...' : ''}`);
 }

 return result;

