// Get all accumulated data and prepare for reporting
const storedData = $getWorkflowStaticData('global');
const allProcessedItems = storedData.accumulatedData || [];

console.log(`\n=== BATCH PROCESSING COMPLETE ===`);
console.log(`Total items processed: ${allProcessedItems.length}`);

// Clear accumulated data for next run
storedData.accumulatedData = [];

// Return all items for report generation
return allProcessedItems;

// Clean empty values from the item
return $input.all().map(item => {
  const cleanedJson = {};
  Object.entries(item.json).forEach(([key, value]) => {
    if (value !== null) {
      if (typeof value === 'number') {
        if (value !== 0 || key === 'Element Count') {
          cleanedJson[key] = value;
        }
      } else if (value !== '') {
        cleanedJson[key] = value;
      }
    }
  });
  return { json: cleanedJson };
});

// Accumulate all processed items
const currentItem = $input.first();
const storedData = $getWorkflowStaticData('global');

// Initialize accumulator if needed
if (!storedData.accumulatedData) {
  storedData.accumulatedData = [];
}

// Add current item to accumulated data
storedData.accumulatedData.push(currentItem);

console.log(`Item processed. Total accumulated: ${storedData.accumulatedData.length} items`);

// Return the current item to continue the flow
return [currentItem];

// Aggregate all results and calculate project totals
const items = $input.all();

// Initialize aggregators
const projectTotals = {
  totalElements: 0,
  totalMass: 0,
  totalCO2: 0,
  totalVolume: 0,
  totalArea: 0,
  byMaterial: {},
  byCategory: {},
  byImpact: {}
};

// Process each item
items.forEach(item => {
  const data = item.json;
  const elementCount = parseFloat(data['Element Count']) || 1;
  const mass = parseFloat(data['Element Mass (tonnes)']) || 0;
  const co2 = parseFloat(data['Total CO2 (tonnes CO2e)']) || 0;
  const volume = parseFloat(data['Volume (mÂ³)']) || 0;
  const area = parseFloat(data['Area (mÂ²)']) || 0;
  const material = data['Material (EU Standard)'] || 'Unknown';
  const category = data['Element Category'] || 'Unknown';
  const impact = data['Impact Category'] || 'Unknown';

  // Update totals
  projectTotals.totalElements += elementCount;
  projectTotals.totalMass += mass;
  projectTotals.totalCO2 += co2;
  projectTotals.totalVolume += volume;
  projectTotals.totalArea += area;

  // Aggregate by material
  if (!projectTotals.byMaterial[material]) {
    projectTotals.byMaterial[material] = {
      elements: 0,
      mass: 0,
      co2: 0,
      volume: 0,
      types: new Set()
    };
  }
  projectTotals.byMaterial[material].elements += elementCount;
  projectTotals.byMaterial[material].mass += mass;
  projectTotals.byMaterial[material].co2 += co2;
  projectTotals.byMaterial[material].volume += volume;
  projectTotals.byMaterial[material].types.add(data['Element Name']);

  // Aggregate by category
  if (!projectTotals.byCategory[category]) {
    projectTotals.byCategory[category] = {
      elements: 0,
      co2: 0
    };
  }
  projectTotals.byCategory[category].elements += elementCount;
  projectTotals.byCategory[category].co2 += co2;

  // Aggregate by impact
  if (!projectTotals.byImpact[impact]) {
    projectTotals.byImpact[impact] = {
      elements: 0,
      co2: 0
    };
  }
  projectTotals.byImpact[impact].elements += elementCount;
  projectTotals.byImpact[impact].co2 += co2;
});

// Add percentages and rankings to each item
const enrichedItems = items.map((item, index) => {
  const data = item.json;
  const co2 = parseFloat(data['Total CO2 (tonnes CO2e)']) || 0;
  const mass = parseFloat(data['Element Mass (tonnes)']) || 0;
  const elementCount = parseFloat(data['Element Count']) || 1;

  return {
    json: {
      ...data,
      // Add project percentages
      'CO2 % of Total': projectTotals.totalCO2 > 0 ? 
        ((co2 / projectTotals.totalCO2) * 100).toFixed(2) : '0.00',
      'Mass % of Total': projectTotals.totalMass > 0 ? 
        ((mass / projectTotals.totalMass) * 100).toFixed(2) : '0.00',
      'Elements % of Total': projectTotals.totalElements > 0 ? 
        ((elementCount / projectTotals.totalElements) * 100).toFixed(2) : '0.00',
      // Add ranking
      'CO2 Rank': index + 1,
      // Project totals (same for all rows)
      'Project Total Elements': projectTotals.totalElements,
      'Project Total Mass (tonnes)': projectTotals.totalMass.toFixed(3),
      'Project Total CO2 (tonnes)': projectTotals.totalCO2.toFixed(3),
      'Project Total Volume (mÂ³)': projectTotals.totalVolume.toFixed(2),
      'Project Total Area (mÂ²)': projectTotals.totalArea.toFixed(2)
    }
  };
});

// Sort by CO2 emissions (highest first)
enrichedItems.sort((a, b) => 
  parseFloat(b.json['Total CO2 (tonnes CO2e)']) - parseFloat(a.json['Total CO2 (tonnes CO2e)'])
);

// Store aggregated data for summary
$getWorkflowStaticData('global').projectTotals = projectTotals;

return enrichedItems;

// Generate professional McKinsey/Accenture style HTML report
const items = $input.all();
