// Parse and enrich AI response with all necessary data
const aiResponse = $input.first().json.output || $input.first().json.response || $input.first().json.text || $input.first().json;
const originalData = $node["Prepare Enhanced Prompts"].json._originalGroupedData || $node["Prepare Enhanced Prompts"].json;

try {
  // Extract JSON from response
  let jsonStr = aiResponse;
  if (typeof jsonStr === 'string') {
    const jsonMatch = jsonStr.match(/```json\n?([\s\S]*?)\n?```/) || jsonStr.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      jsonStr = jsonMatch[1] || jsonMatch[0];
    }
  }

  const analysis = typeof jsonStr === 'string' ? JSON.parse(jsonStr) : jsonStr;

  // Calculate additional metrics - use total CO2 for group
  const co2_kg = analysis.co2_analysis?.total_co2_kg || 0;
  const co2_tonnes = co2_kg / 1000;
  const elementCount = parseFloat(originalData['Element Count']) || 1;
  const co2_per_element = co2_kg / elementCount;

  // Determine impact category
  let impactCategory = 'Unknown';
  const co2Factor = analysis.co2_analysis?.co2_factor_kg_co2_per_kg || 0;
  if (co2Factor < 0) {
    impactCategory = 'Carbon Negative (Storage)';
  } else if (co2Factor <= 0.5) {
    impactCategory = 'Very Low Impact';
  } else if (co2Factor <= 1.0) {
    impactCategory = 'Low Impact';
  } else if (co2Factor <= 2.0) {
    impactCategory = 'Medium Impact';
  } else if (co2Factor <= 5.0) {
    impactCategory = 'High Impact';
  } else {
    impactCategory = 'Very High Impact';
  }

  // Create comprehensive output record
  return [{
    json: {
      // Original data
      ...originalData,

      // Element identification
      'Element Name': analysis.element_identification?.name || originalData['Type Name'] || 'Unknown',
      'Element Category': analysis.element_identification?.category || originalData['Category'] || 'Unknown',
      'Element Type': analysis.element_identification?.type || 'Unknown',
      'Element Function': analysis.element_identification?.function || 'Unknown',

      // Material classification
      'Material (EU Standard)': analysis.material_classification?.european || 'Unknown',
      'Material (DE Standard)': analysis.material_classification?.german || 'Unknown',
      'Material (US Standard)': analysis.material_classification?.us || 'Unknown',
      'Primary Material': analysis.material_classification?.primary_material || 'Unknown',
      'Secondary Materials': (analysis.material_classification?.secondary_materials || []).join(', ') || 'None',

      // Quantities
      'Quantity': analysis.quantities?.value || 0,
      'Quantity Unit': analysis.quantities?.unit || 'piece',
      'Calculation Method': analysis.quantities?.calculation_method || 'Not specified',

      // Dimensions (from raw data) - these are totals
      'Length (mm)': analysis.quantities?.raw_dimensions?.length || originalData['Length'] || 0,
      'Width (mm)': analysis.quantities?.raw_dimensions?.width || originalData['Width'] || 0,
      'Height (mm)': analysis.quantities?.raw_dimensions?.height || originalData['Height'] || 0,
      'Thickness (mm)': analysis.quantities?.raw_dimensions?.thickness || originalData['Thickness'] || 0,
      'Area (mÂ²)': analysis.quantities?.raw_dimensions?.area || originalData['Area'] || 0,
      'Volume (mÂ³)': analysis.quantities?.raw_dimensions?.volume || originalData['Volume'] || 0,

      // CO2 Analysis
      'Material Density (kg/mÂ³)': analysis.co2_analysis?.density_kg_m3 || 0,
      'Element Mass (kg)': analysis.co2_analysis?.mass_kg || 0,
      'Element Mass (tonnes)': (analysis.co2_analysis?.mass_kg || 0) / 1000,
      'CO2 Factor (kg CO2e/kg)': analysis.co2_analysis?.co2_factor_kg_co2_per_kg || 0,
      'Total CO2 (kg CO2e)': co2_kg,
      'Total CO2 (tonnes CO2e)': co2_tonnes,
      'CO2 per Element (kg CO2e)': co2_per_element,
      'CO2 Intensity': analysis.co2_analysis?.co2_intensity_kg_per_unit || 0,
      'Lifecycle Stage': analysis.co2_analysis?.lifecycle_stage || 'A1-A3',
      'Data Source': analysis.co2_analysis?.data_source || 'Industry average',
      'Impact Category': impactCategory,

      // Confidence scores
      'Overall Confidence (%)': analysis.confidence?.overall_score || 0,
      'Material Confidence (%)': analysis.confidence?.material_confidence || 0,
      'Quantity Confidence (%)': analysis.confidence?.quantity_confidence || 0,
      'CO2 Confidence (%)': analysis.confidence?.co2_confidence || 0,
      'Data Quality': analysis.confidence?.data_quality || 'unknown',

      // Metadata
      'Assumptions': (analysis.metadata?.assumptions || []).join('; ') || 'None',
      'Warnings': (analysis.metadata?.warnings || []).join('; ') || 'None',
      'Analysis Notes': analysis.metadata?.notes || '',
      'Processing Timestamp': new Date().toISOString(),
      'Analysis Status': 'Complete'
    }
  }];

} catch (error) {
  // Return error record with original data preserved
  return [{
    json: {
      ...originalData,
      'Analysis Status': 'Failed',
      'Error': error.message,
      'Processing Timestamp': new Date().toISOString()
    }
  }];
}
